(function(jQuery){$.fn.easyOpacity=function(settings){settings=$.extend({opacity:1,hoverOpacity:0.8,duration:500,hoverEffect:true,pointer:false,onMouseOverCallback:null,onMouseOutCallback:null},settings);return this.each(function(){$(this).css('opacity',parseFloat(settings.opacity,10));if(settings.hoverEffect===true){$(this).hover(function(){if(settings.pointer===true){$(this).css('cursor','pointer');}
$(this).stop().animate({opacity:parseFloat(settings.hoverOpacity,10)},settings.duration,settings.onMouseOverCallback);},function(){if(settings.pointer===true){$(this).css('cursor',null);}
$(this).stop().animate({opacity:parseFloat(settings.opacity,10)},settings.duration,settings.onMouseOutCallback);});}});}}($));